import React, { Component } from 'react';
import axios from 'axios';
import Global from '../Global';

class Articles extends Component {

    url = Global.url;

    state ={
        articles: [],
        status: null
    }

    componentDidMount(){
        this.getArticles();
    }

    getArticles=()=>{
        axios.get(this.url+"articles").then(res=>{
            this.setState({
                articles: res.data.articles,
                status: "success"
            });
            console.log(this.state);
        });
    }

    render() {
        if(this.state.articles.length >=1){

            let listArticles = this.state.articles.map((article)=>{
                return (
                    <article className="article-item" id="article-template">
                        <div className="image-wrap">
                            {article.image != null? (<img src={this.url+"get-image/"+article.image} alt={article.title}/>):(<img src='../assets/default.png' alt={article.title}/>)}
                        </div>
                        <h2>{article.title}</h2>
                        <span className="date">
                            {article.date}
                        </span>
                        <a href="#">Leer MÃ¡s</a>

                        <div className="clearfix"></div>

                    </article>
                );
            });

            return(
                <div id="articles">
                    <h2 className="subheader">Listado de Articulos</h2>
                    {listArticles}
                </div>
            );
        }else if(this.state.articles.length === 0 && this.state.status === "success"){
            return(
                <div id="articles">
                    <h2 className="subheader">Listado de Articulos</h2>
                    <p>NO hay articulos para mostrar</p>
                </div>
            );
        }else{
            return(
                <div id="articles">
                    <div class="loading">
                        <img src="https://media.giphy.com/media/3oEjI6SIIHBdRxXI40/giphy.gif" />
                        <p>Esto podrÃ­a tardar un rato</p>
                    </div>
                </div>
            );
        }
        
    }
}
export default Articles;
