import React, { Component } from 'react';
import axios from 'axios';

class Articles extends Component {

    state ={
        articles: [],
        status: null
    }

    componentDidMount(){
        this.getArticles();
    }

    getArticles=()=>{
        axios.get("http://localhost:3900/api/articles").then(res=>{
            this.setState({
                articles: res.data.articles,
                status: "success"
            });
            console.log(this.state);
        });
    }

    render() {
        if(this.state.articles.length >=1){

            let listArticles = this.state.articles.map((article)=>{
                return (
                    <article className="article-item" id="article-template">
                        <div className="image-wrap">
                            <img src="https://aws.traveler.es/prod/designs/v1/assets/1000x667/21250.jpg" alt="Paisaje"/>
                        </div>
                        <h2>Articulo de Prueba</h2>
                        <span className="date">
                            Hace 5 min
                        </span>
                        <a href="#">Leer Más</a>

                        <div className="clearfix"></div>

                    </article>
                );
            });

            return(
                <div id="articles">
                    <h2 className="subheader">Listado de Articulos</h2>
    
                </div>
            );
        }else if(this.state.articles.length === 0 && this.state.status === "success"){
            return(
                <div id="articles">
                    <h2 className="subheader">Listado de Articulos</h2>
                    <p>NO hay articulos para mostrar</p>
                </div>
            );
        }else{
            return(
                <div id="articles">
                    <div class="loading">
                        <img src="https://media.giphy.com/media/3oEjI6SIIHBdRxXI40/giphy.gif" />
                        <p>Esto podría tardar un rato</p>
                    </div>
                </div>
            );
        }
        
    }
}
export default Articles;
