import React,{Component} from 'react';
import {Redirect, Link} from 'react-router-dom';
import axios from 'axios';
import Global from '../Global';
import Sidebar from './Sidebar';

class Article extends Component{

    url = Global.url;

    state={
        article: false,
        status: null
    }

    componentDidMount(){
        this.getArticle();
    }

    getArticle=()=>{
        var id = this.props.match.params.id;

        axios.get(this.url+'article/'+id).then(res=>{
            this.setState({
                article: res.data.article,
                status: 'success'
            });
        });
    }

    render(){
        var article = this.state.article;
        return(
            <div className="center">
                <section id="content">

                    {this.state.article &&
                    
                        <article className="article-item article-detail">
                            <div className="image-wrap">
                                <img src="https://aws.traveler.es/prod/designs/v1/assets/1000x667/21250.jpg" alt="Paisaje"/>
                            </div>
                            <h1 className="subheader">{article.title}</h1>
                            <span className="date">
                                    Hace 5 min
                            </span>
                            <p>
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer et dui ipsum. Vestibulum volutpat, purus nec finibus accumsan, libero enim lobortis ligula, ac maximus mauris lacus non nibh. Nunc commodo libero arcu, sed posuere sapien euismod ut. Integer
                                id justo elit. Proin et ornare nisl, non maximus nisl. Duis sed pellentesque dolor, et venenatis tellus. Nullam mattis, lacus sit amet placerat interdum, elit turpis facilisis lacus, id consectetur massa dui a magna.

                            </p>
                            <p> Fusce pharetra lorem id eros maximus luctus. Maecenas ut felis erat. Integer vestibulum et felis at volutpat. Curabitur quis ligula velit. Ut sollicitudin diam a arcu viverra pellentesque. Donec rutrum dolor blandit, maximus mi non, accumsan
                                leo. Donec faucibus mi sit amet commodo aliquet. Nulla et diam vel nulla suscipit consequat ut at nisi. Etiam lacinia lectus ut mollis gravida. Nulla mattis consequat enim. Phasellus est tellus, fringilla non erat eget, ornare volutpat
                                sem.
                            </p>
                            <div className="clearfix"></div>

                        </article>
                    }
                    {!this.state.article &&
                        <div>
                            <h1 className="subheader">NO se encontró el articulo</h1>
                            <p>El articulo no existe</p>
                        </div>

                    }
                   {!this.state.status == null &&
                       <div id="article">
                            <div className="loading">
                                <img src="https://media.giphy.com/media/3oEjI6SIIHBdRxXI40/giphy.gif" />
                                <p>Esto podría tardar un rato</p>
                            </div>
                        </div>

                    }

                </section>

                <Sidebar />

                <div className="clearfix"></div>
            </div>
        );
    }
}
export default Article;
