import React, {Component} from 'react';
import {BrowserRouter, Route, Switch} from 'react-router-dom';
import Pruebas from './components/Pruebas';
import MiComponente from './components/MiComponente';
import Peliculas from './components/Peliculas';
import Error from './components/Error';

class Router extends Component{
    render(){
        return(
            <BrowserRouter>
                {/*Configurar rutas y paginas (importar el react-router-dom)*/}

                <Switch>
                    <Route exact path="/" component={Peliculas}></Route>
                    <Route exact path="/ruta-prueba" component={Pruebas}></Route>
                    <Route exact path="/ruta-componente" component={MiComponente}></Route>

                    <Route exact path="/pagina-1" render={()=>(
                        <React.Fragment>
                            <section id="content">
                                <h2 className="subheader">Pagina #1</h2>
                                <p>Soy la pagina #1.</p>
                            </section>
                        </React.Fragment>
                        
                    )}/>

                    <Route exact path="/pruebas" render={()=>(
                        <React.Fragment>
                            <section id="content">
                                <h2 className="subheader">Pagina De pruebas</h2>
                                <p>Soy la pag de pruebas</p>
                            </section>
                        </React.Fragment>
                        
                    )}/>

                    <Route component={Error}></Route>

                </Switch>
            </BrowserRouter>
        );
    }
}
export default Router;
