import React, {Component} from 'react';
import MiComponente from './MiComponente';
import Peliculas from './Peliculas';

class Pruebas extends Component{

    contador =0;

    constructor(props){
        super(props);
        this.state ={
            contador: 0
        };
    }

    sumar(){
        //this.contador+=1;
        this.setState({
            contador: (this.state.contador+1)
        });
    }

    restar(){
        //this.contador-=1;
        this.setState({
            contador: (this.state.contador+1)
        });
    }

    render(){
        
        return(
            <section id="content">
          
                <h2 className="subheader">Secci√≥n de Pruebas</h2>



                <MiComponente />
                <Peliculas />


                <h2 className="subheader">Estado</h2>
                <p>
                    Contado: {this.state.contador}
                </p>
                <input type="button" value="Sumar" onClick={this.sumar.bind(this)}></input>
                <input type="button" value="Restar" onClick={this.restar.bind(this)}></input>
            </section>
        );
    }

}

export default Pruebas;
