import React, {Component} from 'react';
import MiComponente from './MiComponente';
import Peliculas from './Peliculas';

class Pruebas extends Component{

    contador =0;

    sumar(){
        this.contador+=1;
    }

    restar(){
        this.contador-=1;
    }

    render(){
        
        return(
            <section id="content">
          
                <h2 class="subheader">Secci√≥n de Pruebas</h2>



                <MiComponente />
                <Peliculas />


                <h2 class="subheader">Estado</h2>
                <p>
                    Contado: {this.contador}
                </p>
                <input type="button" value="Sumar" onClick={sumar}></input>
                <input type="button" value="Restar" onClick={restar}></input>
            </section>
        );
    }

}

export default Pruebas;
